function App() {


    const[time,setTime]=useState(new Date().toLocaleString('en-US', { hour: 'numeric', hour12: true }));

    const UpdateTime = () => {
        setTime(new Date().toLocaleString('en-US', { hour: 'numeric', hour12: true }));
    };
    setInterval(UpdateTime,1000)

    return (
        <>
          <h1>{time}</h1>
        </>

