import React from 'react';
import ReactDOM from 'react-dom';
import "./index.css";

const date = new Date(2020,12,31,20);
const currentHour = date.getHours();
let greet = " "
const greetColour = {};

if (currentHour >= 1 && currentHour <= 11) {
   greet = "Good Morning";
   greetColour.color = "#041C32"
}

else if (currentHour >= 12 && currentHour <= 19) {
   greet = "Good Afternoon";
   greetColour.color = "#395B64"
}

else {
   greet = "Good Night";
   greetColour.color = "#219F94"
}

ReactDOM.render(
   <>

   <span style={{backgroundColor:"#D1D1D1", padding:"1rem",borderRadius:"0.8rem",}}>
     <span style={{color:"#1572A1"}}>Hello sir,</span><span style={greetColour}> {greet}</span>
   </span>


   </>
   , document.getElementById("root")
);
