import React, { useState } from 'react';
import "./index.css"


function App() {
    const [input, setInput] = useState("");
    const [addVal, setAddVal] = useState([]);
    const InputVal = (e) => {
        setInput(e.target.value)
    }
    const AddInput = () => {
        setAddVal((preVal) => {
            return [...preVal, input]
        })
        setInput("");
    }
    const DeleteItem = () => {

    }



    return (
        <>
            <div className='background'>
                <div className='card'>

                    <div className='cardTitle'>To Do List</div>

                    <input className='addContent' type="text" placeholder='Add a Item' onChange={InputVal} value={input} />
                    <button className='addBtn' onClick={AddInput}> + </button>

                    {addVal.map((crntVal,idx) => {
                        return (
                            <>
                                <div key={idx} className='listItem'>
                                    <button className='deleteBtn' onClick={DeleteItem}> &#10540; </button>
                                    <li className='list'>{crntVal} </li>
                                </div>
                            </>);
                    })}

                </div>

            </div>
        </>
    );
}

export default App;
