import React, { useState } from 'react';
import "./index.css"
import ToDoItems from './ToDoItems';


function App() {
    const [input, setInput] = useState("");
    const [addVal, setAddVal] = useState([]);
    const InputVal = (e) => {
        setInput(e.target.value)
    }
    const AddInput = () => {
        setAddVal((preVal) => {
            return [...preVal, input]
        })
        setInput("");
    }
    const DeleteItem = (id) => {
        setAddVal((preVal)=>{
            return preVal.filter((item,idx)=>{
                return idx !== id;
            })
        })
    }



    return (
        <>
            <div className='background'>
                <div className='card'>

                    <div className='cardTitle'>To Do List</div>

                    <input className='addContent' type="text" placeholder='Add a Item' onChange={InputVal} value={input} />
                    <button className='addBtn' onClick={AddInput}> + </button>

                    {addVal.map((crntVal, idx) => {
                        return (
                            <ToDoItems 
                                id = {idx}
                                currentItem = {crntVal}
                                onSelect = {DeleteItem}
                            />

                        );
                    })}

                </div>

            </div>
        </>
    );
}

export default App;
