import React from 'react';
import ReactDOM from 'react-dom';
import "./index.css";

const date = new Date(2002,12,31,12);
const currentHour = date.getHours();
let greet = " "
const greetColour = {};

if (currentHour >= 1 && currentHour <= 11) {
   greet = "Good Morning";
   greetColour.color = "red"
}

else if (currentHour >= 12 && currentHour <= 19) {
   greet = "Good Afternoon";
   greetColour.color = "green"
}

else {
   greet = "Good Night";
   greetColour.color = "yellow"
}

ReactDOM.render(
   <>

   <span style={{backgroundColor:"pink", padding:"1rem",borderRadius:"0.8rem",}}>
     <span>Hello sir,</span><span style={greetColour}> {greet}</span>
   </span>


   </>
   , document.getElementById("root")
);
