import "./index.css"


function App() {
    const [input, setInput] = useState("");
    const [addVal, setAddVal] = useState([]);
    const InputVal = (e) => {
        setInput(e.target.value)
    }
    const AddInput = () => {
        setAddVal((preVal) => {
            return [...preVal, input]
        })
        setInput("");
    }
    const DeleteItem = (id) => {
