                    {addVal.map((crntVal, idx) => {
                        return (

                            <div key={idx} className='listItem'>
                                <button className='deleteBtn' onClick={DeleteItem(idx)}> &#10540; </button>
                                <li className='list'>{crntVal} </li>
                            </div>
                        );
                    })}

                </div>

            </div>
        </>
    );
}

export default App;
